import React, { createContext, Component } from 'react';

export const RecordsContext = createContext();

class RecordsContextProvider extends Component {

    state = {
        runningRecords: {},
        swimmingRecords: {},
        cyclingRecords: {},
        triathlonRecords: {}
    };

    addRecord = (activity, date, distance, time) => {
        switch (activity) {
            case 'Running':
                this.setState({
                    runningRecords: [...this.state.runningRecords, {
                        date: date,
                        distance: distance,
                        time: time
                    }]
                });
                break;
            case 'Swimming':
                this.setState({
                    swimmingRecords: [...this.state.swimmingRecords, {
                        date: date,
                        distance: distance,
                        time: time
                    }]
                });
                break;
            case 'Cycling':
                this.setState({
                    cyclingRecords: [...this.state.cyclingRecords, {
                        date: date,
                        distance: distance,
                        time: time
                    }]
                });
                break;
            case 'Triathlon':
                this.setState({
                    triathlonRecords: [...this.state.triathlonRecords, {
                        date: date,
                        distance: distance,
                        time: time
                    }]
                });
                break;
