        }
    };

    render() {
        return (
            <RecordsContext.Provider value={{
                ...this.state,
                addRecord: this.addRecord
            }}>
                {this.props.children}
            </RecordsContext.Provider>
        );
    }
}

export default RecordsContextProvider;
