import React, { createContext, Component } from 'react';

export const RacesContext = createContext();

class RacesContextProvider extends Component {

    state = {
        races: []
    };

    // addRace = (name, type, date, distance) => {
    //     this.setState( {races: [...this.state.races, {
    //         name: name,
    //         type: type,
    //         date: date,
    //         distance: distance
    //     }]} )
    // }

    render() {
        return(
            <RacesContext.Provider value={ {...this.state} }>
                {this.props.children}
            </RacesContext.Provider>
        );
    }
}

export default RacesContextProvider;

